//! ScannerAPI JS client (minified)
(function(t){t.scanOptions={};t.scan=async function(o){const r=localStorage.getItem('jwtToken');
return fetch('/api/scanner/scan',{method:'POST',headers:{'Content-Type':'application/json','Authorization':'Bearer '+r},body:JSON.stringify(o)})
.then(e=>e.json()).then(e=>e.data)};t.getResult=async function(id){const r=localStorage.getItem('jwtToken');
return fetch(`/api/scanner/${id}`,{headers:{'Authorization':'Bearer '+r}})
.then(e=>e.json()).then(e=>e.data)};})(window.ScannerAPI||(window.ScannerAPI={}));